func TestRsaSignBase64(t *testing.T) {
	var priKey = ""
	var content = "a=123"
	signature, error := RsaSignBase64(content, priKey, crypto.SHA256)
	if nil != error {
		t.Fatal("exception happened")
	}

	if 0 == len(signature) {
		t.Fatal("signature is empty")
	}
	t.Log(signature)
}

